def division(numerador, denominador):
  if denominador == 0:
    raise Exception("El denominador no puede ser 0")

  return numerador/denominador

print(division(10, 0))

def division(numerador, denominador):
  if denominador == 0:
    raise TypeError("El denominador no puede ser 0")

  return numerador/denominador

print(division(10, 0))
